import React, { Component } from 'react'
import './styles/App.css'
import Movie from './components/Movie'

import data from './components/Data'
export default class App extends Component {

       state={
           query:''
       }

       searchText=(e)=>{
          this.setState({
            query:e.target.value
          })

       }

    render() {

        let name=[];
        let year=[];
        Object.keys(data).forEach((value)=>{
            name.push(data[value].title)
            year.push(data[value].year)
        })

        //  console.log(name)
        //  console.log(year)



        let newName=name.filter((value)=>{
            return value.toLowerCase().indexOf(this.state.query)!==-1;
        })


        return (
            <div>
                    <div className='input-container'>
                            <label>Search For a Movie</label>
                            <input type='text' onKeyUp={this.searchText}/>
                    </div>

                       <div className='movies-container'>
                           
                                      
                                      {
                                          name.map((value,index)=>{
                                              return  <Movie   name={value}  title={name[index]} year={year[index]} key={index}/>
                                          })
                                      }
                       </div>     



                    


                   
                    
            </div>
        )
    }
}
