import React, { Component } from 'react'
import ImageSearch from './ImageSearch/ImageSearch'

const API_KEY='18236546-05683ef082b0902922b657844'

const url=`https://pixabay.com/api/?key=${API_KEY}&q=yellow+flowers&image_type=photo`
               


 class App extends Component {




      state={
          images:[]
      }   

    handleRequest= async (e)=>{
        e.preventDefault()
        const request=await fetch(url)
        .then((res)=>{
            return res.json()
        })
        .then((data)=>{
           
            this.setState({
                images:data.hits
            })
           
        })
       
    }

  

   

    render() {
        return (
            <div> 
                        <ImageSearch handleSubmit={this.handleRequest}/>
            </div>
        )
    }
}



export default App
