import React, { Component } from 'react'
import ImageSearch from './ImageSearch/ImageSearch'
import ImageList from './Imagelist/ImageList'

const API_KEY='18236546-05683ef082b0902922b657844'
 class App extends Component {

      state={
          images:[]
      }   

    handleRequest= async (e)=>{
        e.preventDefault()
        let search=e.target.searchValue.value;
        const url=`https://pixabay.com/api/?key=${API_KEY}&q=${search}&image_type=photo`;
       
        const request=await fetch(url)
        const response=await request.json()
        this.setState({
            images:response.hits
        })
     
        console.log(this.state.images)
    }

   

   

    render() {
        return (
            <div> 
                        <ImageSearch handleSubmit={this.handleRequest}/>
                         <ImageList images={this.state.images}/>
            </div>
        )
    }
}



export default App
