import React, { Component } from 'react'
import Navbar from './Navbar'
import SearchArea from './SearchArea'



const apikey='d092f6df94edf70406673e3eddba17d3';

export default class App extends Component {
    state={
        movies:[],
        searchTerm:''
    }

    

    
    handleSubmit=(e)=>{
        e.preventDefault()
        fetch(`https://api.themoviedb.org/3/movie/550?api_key=${apikey}&query=${this.state.searchTerm}`)
        .then((res)=>{
            return res.json()
        })
        .then((data)=>{
            console.log(data)
           
                // movies:[...data.results]
                // this.setState({ movies: [...data.results]})
          
        })
    }

    handleChange=(e)=>{
           this.setState({
               searchTerm:e.target.value
           })
    }
    render() {
        return (
            <div>
                   <Navbar/>
                    <SearchArea handleSubmit={this.handleSubmit} handleChange={this.handleChange}/>
            </div>
        )
    }
}
